(()=>{"use strict";class t{constructor(){}static textToPixel(t,e=16){const n=document.createElement("canvas"),i=n.getContext("2d");n.width=e*t.length,n.height=e,i.font=`${e}px sans-serif`,i.fillText(t,0,e);const o=i.getImageData(0,0,n.width,n.height),s=[];for(let t=0;t<n.height;t++){const e=[];for(let i=0;i<n.width;i++){const s=4*(t*n.width+i),c=o.data[s+3];e.push(c>0?1:0)}s.push(e)}return s}}const e=document.getElementById("input-text"),n=document.getElementById("pixel-display");e.addEventListener("input",(()=>{const i=e.value,o=t.textToPixel(i).map((t=>t.join(""))).join("\n");n.textContent=o}))})();